option.value:
/**
 * ============================================
 * PERBAIKAN BACKEND: DELETE BARANG
 * ============================================
 * 
 * MASALAH: Barang tidak bisa dihapus karena Foreign Key Constraint
 *          dari tabel order_detail yang referensi ke barang_id
 * 
 * SOLUSI: Cek dulu apakah barang dipakai di order,
 *         jika iya, beri pesan error yang jelas
 * 
 * ============================================
 * COPY CODE INI KE FILE BACKEND ANDA
 * ============================================
 */

// ============================================
// OPSI 1: CHECK DULU SEBELUM DELETE (RECOMMENDED)
// ============================================

app.delete('/api/barang/:id', async (req, res) => {
    const { id } = req.params;

    // Validasi ID
    if (!id || isNaN(id)) {
        return res.status(400).json({ 
            success: false,
            message: 'ID barang tidak valid' 
        });
    }

    try {
        // STEP 1: Cek apakah barang digunakan di order_detail
        const [orderCheck] = await db.query(
            'SELECT COUNT(*) as count FROM order_detail WHERE barang_id = ?',
            [id]
        );

        if (orderCheck[0].count > 0) {
            // Barang masih dipakai di order - TIDAK BISA DIHAPUS
            return res.status(409).json({
                success: false,
                message: `Barang tidak bisa dihapus karena sudah digunakan dalam ${orderCheck[0].count} order. Hapus order terkait terlebih dahulu.`
            });
        }

        // STEP 2: Cek apakah barang ada
        const [barangCheck] = await db.query(
            'SELECT * FROM barang WHERE barang_id = ?',
            [id]
        );

        if (barangCheck.length === 0) {
            return res.status(404).json({
                success: false,
                message: 'Barang tidak ditemukan'
            });
        }

        // STEP 3: Hapus gambar jika ada (optional)
        if (barangCheck[0].gambar_url) {
            const fs = require('fs');
            const path = require('path');
            const imagePath = path.join(__dirname, 'public', barangCheck[0].gambar_url);

            if (fs.existsSync(imagePath)) {
                fs.unlinkSync(imagePath);
                console.log('Gambar berhasil dihapus:', imagePath);
            }
        }

        // STEP 4: Hapus barang
        const [result] = await db.query(
            'DELETE FROM barang WHERE barang_id = ?',
            [id]
        );

        if (result.affectedRows === 0) {
            return res.status(404).json({
                success: false,
                message: 'Barang tidak ditemukan atau sudah dihapus'
            });
        }

        // SUCCESS
        console.log(`Barang dengan ID ${id} berhasil dihapus`);
        res.status(200).json({
            success: true,
            message: 'Barang berhasil dihapus'
        });

    } catch (error) {
        console.error('Error deleting barang:', error);
        res.status(500).json({
            success: false,
            message: 'Gagal menghapus barang: ' + error.message
        });
    }
});


// ============================================
// OPSI 2: SOFT DELETE (Tandai sebagai dihapus)
// ============================================
// Jika Anda ingin tetap menyimpan data barang tapi tidak ditampilkan

/*
STEP 1: Tambah kolom is_deleted di tabel barang:

ALTER TABLE barang ADD COLUMN is_deleted TINYINT(1) DEFAULT 0;

STEP 2: Ganti DELETE menjadi UPDATE:
*/

app.delete('/api/barang/:id', async (req, res) => {
    const { id } = req.params;

    try {
        // Soft delete - tandai sebagai dihapus
        const [result] = await db.query(
            'UPDATE barang SET is_deleted = 1 WHERE barang_id = ?',
            [id]
        );

        if (result.affectedRows === 0) {
            return res.status(404).json({
                success: false,
                message: 'Barang tidak ditemukan'
            });
        }

        res.status(200).json({
            success: true,
            message: 'Barang berhasil dihapus'
        });

    } catch (error) {
        console.error('Error soft-deleting barang:', error);
        res.status(500).json({
            success: false,
            message: 'Gagal menghapus barang: ' + error.message
        });
    }
});

// STEP 3: Update GET untuk tidak menampilkan barang yang sudah dihapus:
app.get('/api/barang', async (req, res) => {
    try {
        const [rows] = await db.query(
            'SELECT * FROM barang WHERE is_deleted = 0 OR is_deleted IS NULL'
        );
        res.json(rows);
    } catch (error) {
        res.status(500).json({ message: error.message });
    }
});


// ============================================
// OPSI 3: CASCADE DELETE (Hapus order_detail juga)
// ============================================
// PERINGATAN: Ini akan menghapus data order yang terkait!

/*
STEP 1: Ubah struktur tabel order_detail:

ALTER TABLE order_detail 
DROP FOREIGN KEY order_detail_ibfk_2;  -- nama constraint mungkin berbeda

ALTER TABLE order_detail 
ADD CONSTRAINT order_detail_barang_fk 
FOREIGN KEY (barang_id) REFERENCES barang(barang_id) ON DELETE CASCADE;

Dengan ini, saat barang dihapus, order_detail yang terkait juga ikut terhapus.
HATI-HATI: Data order akan hilang!
*/


// ============================================
// OPSI 4: SET NULL pada order_detail
// ============================================
// Order tetap ada, tapi barang_id jadi NULL

/*
STEP 1: Ubah kolom barang_id menjadi nullable:

ALTER TABLE order_detail MODIFY barang_id INT NULL;

STEP 2: Ubah foreign key:

ALTER TABLE order_detail 
DROP FOREIGN KEY order_detail_ibfk_2;

ALTER TABLE order_detail 
ADD CONSTRAINT order_detail_barang_fk 
FOREIGN KEY (barang_id) REFERENCES barang(barang_id) ON DELETE SET NULL;

Dengan ini, saat barang dihapus, barang_id di order_detail jadi NULL.
*/

package com.example.projectpammysql.viewmodel

import android.util.Log
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.projectpammysql.data.models.BarangRequest
import com.example.projectpammysql.data.models.BarangResponse
import com.example.projectpammysql.data.repositories.RepositoryBarang
import com.example.projectpammysql.utils.TokenManager
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.first
import kotlinx.coroutines.launch

sealed class BarangUiState {
    object Idle : BarangUiState()
    object Loading : BarangUiState()
    data class SuccessList(val barangList: List<BarangResponse>) : BarangUiState()
    data class SuccessDetail(val barang: BarangResponse) : BarangUiState()
    data class Success(val message: String) : BarangUiState()
    data class Error(val message: String) : BarangUiState()
}

class BarangViewModel(
    private val repositoryBarang: RepositoryBarang,
    private val tokenManager: TokenManager
) : ViewModel() {

    private val _uiState = MutableStateFlow<BarangUiState>(BarangUiState.Idle)
    val uiState: StateFlow<BarangUiState> = _uiState

    fun getAllBarang() {
        viewModelScope.launch {

            _uiState.value = BarangUiState.Loading

            try {
                val response = repositoryBarang.getAllBarang()
                Log.d("BarangViewModel", "Response received: ${response.code()}")
                Log.d("BarangViewModel", "Response body: ${response.body()}")

                if (response.isSuccessful && response.body() != null) {
                    val barangList = response.body()!!
                    Log.d("BarangViewModel", "Barang list size: ${barangList.size}")
                    barangList.forEachIndexed { index, barang ->
                        Log.d("BarangViewModel", "Barang $index: ${barang.namaBarang}, stok: ${barang.stok}, harga: ${barang.harga}")
                    }
                    _uiState.value = BarangUiState.SuccessList(barangList)
                    Log.d("BarangViewModel", "State set to SuccessList")
                } else {
                    Log.e("BarangViewModel", "Response not successful: ${response.message()}, code: ${response.code()}")
                    _uiState.value = BarangUiState.Error("Gagal mengambil data: ${response.message()}")
                }
            } catch (e: Exception) {
                Log.e("BarangViewModel", "Exception in getAllBarang: ${e.message}", e)
                _uiState.value = BarangUiState.Error("Error: ${e.message}")
            }
        }
    }

    fun getBarangById(id: Int) {
        viewModelScope.launch {
            _uiState.value = BarangUiState.Loading
            try {
                val response = repositoryBarang.getBarangById(id)
                if (response.isSuccessful && response.body() != null) {
                    _uiState.value = BarangUiState.SuccessDetail(response.body()!!)
                } else {
                    _uiState.value = BarangUiState.Error("Gagal mengambil detail: ${response.message()}")
                }
            } catch (e: Exception) {
                _uiState.value = BarangUiState.Error("Error: ${e.message}")
            }
        }
    }

    fun createBarang(barangRequest: BarangRequest) {
        viewModelScope.launch {
            _uiState.value = BarangUiState.Loading
            try {
                val response = repositoryBarang.createBarang(barangRequest)
                if (response.isSuccessful) {
                    _uiState.value = BarangUiState.Success("Barang berhasil ditambahkan")
                } else {
                    _uiState.value = BarangUiState.Error("Gagal menambah barang: ${response.message()}")
                }
            } catch (e: Exception) {
                _uiState.value = BarangUiState.Error("Error: ${e.message}")
            }
        }
    }

    fun updateBarang(id: Int, barangRequest: BarangRequest) {
        viewModelScope.launch {
            Log.d("BarangViewModel", "updateBarang called for id: $id")
            _uiState.value = BarangUiState.Loading
            try {
                Log.d("BarangViewModel", "Updating barang: ${barangRequest.namaBarang}")
                val response = repositoryBarang.updateBarang(id, barangRequest)
                Log.d("BarangViewModel", "Update response code: ${response.code()}")

                if (response.isSuccessful) {
                    Log.d("BarangViewModel", "Barang updated successfully")
                    _uiState.value = BarangUiState.Success("Barang berhasil diperbarui")
                } else {
                    Log.e("BarangViewModel", "Update failed: ${response.message()}")
                    _uiState.value = BarangUiState.Error("Gagal memperbarui barang: ${response.message()}")
                }
            } catch (e: Exception) {
                Log.e("BarangViewModel", "Exception in updateBarang: ${e.message}", e)
                _uiState.value = BarangUiState.Error("Error: ${e.message}")
            }
        }
    }

    fun deleteBarang(id: Int) {
        viewModelScope.launch {
            Log.d("BarangViewModel", "deleteBarang called for id: $id")
            _uiState.value = BarangUiState.Loading
            try {
                val response = repositoryBarang.deleteBarang(id)
                Log.d("BarangViewModel", "Delete response code: ${response.code()}")

                if (response.isSuccessful) {
                    Log.d("BarangViewModel", "Barang deleted successfully")
                    _uiState.value = BarangUiState.Success("Barang berhasil dihapus")
                } else {
                    // ✅ Handle foreign key constraint error (409)
                    val errorMessage = if (response.code() == 409) {
                        "Barang tidak bisa dihapus karena masih digunakan di order. Silakan hapus order terlebih dahulu."
                    } else {
                        "Gagal menghapus barang: ${response.message()}"
                    }
                    Log.e("BarangViewModel", "Delete failed: $errorMessage")
                    _uiState.value = BarangUiState.Error(errorMessage)
                }
            } catch (e: Exception) {
                Log.e("BarangViewModel", "Exception in deleteBarang: ${e.message}", e)
                _uiState.value = BarangUiState.Error("Error: ${e.message}")
            }
        }
    }

    fun resetState() {
        _uiState.value = BarangUiState.Idle
    }
}

